import './Navigation.scss';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

const StyledMenu = withStyles({
  paper: {
    border: '1px solid #d3d4d5',
  },
})((props) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    {...props}
  />
));

const StyledMenuItem = withStyles((theme) => ({
  root: {
    '&:focus': {
      backgroundColor: theme.palette.primary.main,
      '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
        color: theme.palette.common.white,
      },
    },
  },
}))(MenuItem);

export default function Navigation() {

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };


  return (
    <div className="navbar">
      <Link to="/"><img className="logo" src="/images/logo.png" /></Link>
      <ul className="list-action">
        <Link to="/mission"><li className="action-li">Notre mission</li></Link>

        <div>
      <Button
        aria-controls="customized-menu"
        aria-haspopup="true"
        variant="contained"
        color="primary"
        onClick={handleClick}
      >
        Les activités
      </Button>
      <StyledMenu
        id="customized-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <StyledMenuItem>
          <ListItemIcon>
          </ListItemIcon>
          <Link to="/activites"><li className="action-subli">Canot</li></Link>
          {/* <ListItemText primary="Canot/Camping" /> */}
        </StyledMenuItem>
        <StyledMenuItem>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Tubes" />
        </StyledMenuItem>
        <StyledMenuItem>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Shuttle" />
        </StyledMenuItem>
      </StyledMenu>
    </div>
          <li className="action-li">Les activités
            <ul>
              <Link to="/activites"><li className="action-subli">Tubes</li></Link>
            </ul>
          </li>


        <Link to="/site"><li className="action-li">Le site</li></Link>
        <Link to="/reservations"><li className="action-li">Réservations</li></Link>
        <Link to="/contact"><li className="action-li">Nous contacter</li></Link>
      </ul>
    </div>
  )
}
