import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { Link } from 'react-router-dom';

const StyledMenu = withStyles({
  paper: {
    border: '1px solid #d3d4d5',
    width: 'fit-content',

  },
})((props) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    {...props}
  />
));

const StyledMenuItem = withStyles((theme) => ({
  root: {
    fontFamily: "'Raleway', serif",
    fontSize: "1.5rem",
    '&:focus': {
      opacity: '0.6',
      fontWeight: 'bold',
      '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
        color: theme.palette.common.white,
      },
    },
  },
}))(MenuItem);
export default function DropDownMenu(props) {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <Button
        aria-controls="customized-menu"
        aria-haspopup="true"
        variant="contained"
        color="primary"
        onClick={handleClick}
      >
        {props.title}}
      </Button>
      <StyledMenu
        id="customized-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
            <StyledMenuItem>
              <Link to="/mission"><li className="action-subli" style={{color: "black"}}>{props.first}</li></Link>
            </StyledMenuItem>
            <StyledMenuItem>
                <Link to="/region"><li className="action-subli" style={{color: "black"}}>{props.second}</li></Link>
            </StyledMenuItem>
            <StyledMenuItem>
                <Link to="/securite"><li className="action-subli" style={{color: "black"}}>{props.third}</li></Link>
            </StyledMenuItem>
          </StyledMenu>
    </div>
  );
}
